import com.bridgelabz.parkinglot.enums.DriverType;import com.bridgelabz.parkinglot.exception.ParkingLotException;import com.bridgelabz.parkinglot.model.parkingSpotDetails;import com.bridgelabz.parkinglot.observer.AirportSecurityImpl;import com.bridgelabz.parkinglot.observer.ParkingOwnerImpl;import com.bridgelabz.parkinglot.service.ParkingLotSystem;import org.junit.Assert;import org.junit.Before;import org.junit.Test;import java.time.LocalDateTime;import java.time.format.DateTimeFormatter;public class ParkingLotSystemTest {    ParkingLotSystem parkingLotSystem;    @Before    public void setUp() {        parkingLotSystem = new ParkingLotSystem(100, 4);    }    @Test    public void givenAVehicle_IfParked_ShouldReturnTrue() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            boolean parkingStatus = ParkingLotSystem.LatestVehicleStatus;            Assert.assertTrue(parkingStatus);        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_ALREADY_PARKED, e.type);        }    }    @Test    public void givenAVehicle_IfUnParked_ShouldReturnFalse() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-1111", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.unPark("MH-2222");            boolean unParkingStatus = ParkingLotSystem.LatestVehicleStatus;            Assert.assertFalse(unParkingStatus);        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_ALREADY_PARKED, e.type);            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_NOT_PARKED, e.type);        }    }    @Test    public void givenAVehicle_IfNotPresentAndWantToUnParke_ShouldHandleException() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.unPark("MH-1111");        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_NOT_PARKED, e.type);        }    }    @Test    public void givenVehicle_IfParkingLotIsFull_ShouldHandleException() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-1111", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-3333", DriverType.NON_HANDICAP_DRIVER));        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.PARKING_LOT_IS_FULL, e.type);        }    }    @Test    public void givenVehicle_IfParkingLotIsFullInformToOwner_ShouldHandleException() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-1111", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-3333", DriverType.NON_HANDICAP_DRIVER));        } catch (ParkingLotException e) {            Assert.assertTrue(ParkingOwnerImpl.status);            Assert.assertEquals(ParkingLotException.ExceptionType.PARKING_LOT_IS_FULL, e.type);        }    }    @Test    public void givenVehicle_IfAlreadyPresent_ShouldHandleException() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_ALREADY_PARKED, e.type);        }    }    @Test    public void givenVehicle_IfParkingLotIsFullInformToAirportSecurity_ShouldHandleException() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-1111", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-3333", DriverType.NON_HANDICAP_DRIVER));        } catch (ParkingLotException e) {            Assert.assertTrue(AirportSecurityImpl.status);            Assert.assertEquals(ParkingLotException.ExceptionType.PARKING_LOT_IS_FULL, e.type);        }    }    @Test    public void givenVehicle_IfParkingLotNowAvailable_ShouldReturnFalse() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-1111", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-3333", DriverType.NON_HANDICAP_DRIVER));        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.PARKING_LOT_IS_FULL, e.type);            try {                parkingLotSystem.unPark("MH-1111");            } catch (ParkingLotException exception) {                exception.printStackTrace();            }            Assert.assertFalse(ParkingOwnerImpl.status);        }    }    @Test    public void givenVehicle_IfSpaceIsAvailable_ShouldUpdateParkingLotNumberNoToOwner() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            int slotNo = parkingLotSystem.allocateAvailableLot(new parkingSpotDetails("MH-3333", DriverType.NON_HANDICAP_DRIVER));            Assert.assertEquals(101, slotNo);        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_ALREADY_PARKED, e.type);        }    }    @Test    public void givenVehicle_AlreadyParkedAndWantsToAllocateLot_ShouldHandleException() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.allocateAvailableLot(new parkingSpotDetails("MH-3333", DriverType.NON_HANDICAP_DRIVER));        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_ALREADY_PARKED, e.type);        }    }    @Test    public void givenVehicle_CheckVehiclePresentOrNotIfParked_ShouldReturnParkingLotNumber() {        try {            parkingLotSystem.park(new parkingSpotDetails("A", DriverType.NON_HANDICAP_DRIVER));            boolean lotNo = parkingLotSystem.isMyVehiclePresent("A");            Assert.assertTrue(lotNo);        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_NOT_PARKED, e.type);        }    }    @Test    public void givenVehicle_CheckVehiclePresentOrNotIfNotParked_ShouldHandleException() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            boolean parkingStatusOfVehicle = parkingLotSystem.isMyVehiclePresent("MH-2222");            Assert.assertTrue(parkingStatusOfVehicle);        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_NOT_PARKED, e.type);        }    }    @Test    public void givenVehicleDetails_IfVehicleIsParkedAlready_ShouldReturnParkingTime() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-1111", DriverType.NON_HANDICAP_DRIVER));            LocalDateTime parkedTime = parkingLotSystem.vehicleArrivedTime("MH-1111");            DateTimeFormatter format1 = DateTimeFormatter.ofPattern("dd-MM-yyyy HH:mm");            String parkedDateTime = parkedTime.format(format1);            LocalDateTime currentTime = LocalDateTime.now();            DateTimeFormatter format2 = DateTimeFormatter.ofPattern("dd-MM-yyyy HH:mm");            String currentDateTime = currentTime.format(format2);            Assert.assertEquals(parkedDateTime, currentDateTime);        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_NOT_PARKED, e.type);        }    }    @Test    public void givenVehicleDetails_IfVehicleIsNotParkedWhileCheckingForVehicleTiming_ShouldReturnHandleException() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-2222", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-6666", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.vehicleArrivedTime("MH-1111");        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_NOT_PARKED, e.type);        }    }    @Test    public void givenVehicleDetails_IfVehicleParked_ShouldParkEvenly() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-11", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-22", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-33", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-44", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-55", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-66", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-67", DriverType.NON_HANDICAP_DRIVER));            int lotNo = ParkingOwnerImpl.lotNum;            Assert.assertEquals(3, lotNo);        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_ALREADY_PARKED, e.type);        }    }    @Test    public void givenVehicleDetails_IfVehicleAlreadyParked_ShouldHandleException() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-11", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-22", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-33", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.allocateAvailableLot(new parkingSpotDetails("MH-33", DriverType.NON_HANDICAP_DRIVER));        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_ALREADY_PARKED, e.type);        }    }    @Test    public void givenVehicle_IfDriverTypeIsHandicap_ShouldParkToNearestLot() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-11", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-22", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-33", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-44", DriverType.HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-55", DriverType.NON_HANDICAP_DRIVER));            int lotNo1 = ParkingOwnerImpl.lotNum;            Assert.assertEquals(4, lotNo1);        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_ALREADY_PARKED, e.type);        }    }    @Test    public void givenVehicle_IfVehicleDriverTypeIsHandicap_ShouldParkToNearestLot() {        try {            parkingLotSystem.park(new parkingSpotDetails("MH-11", DriverType.NON_HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-44", DriverType.HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-33", DriverType.HANDICAP_DRIVER));            parkingLotSystem.park(new parkingSpotDetails("MH-55", DriverType.NON_HANDICAP_DRIVER));            int allottedLotNo = ParkingOwnerImpl.lotNum;            Assert.assertEquals(2, allottedLotNo);        } catch (ParkingLotException e) {            Assert.assertEquals(ParkingLotException.ExceptionType.VEHICLE_ALREADY_PARKED, e.type);        }    }}